        var startId;
        var index;
        $(function () {
            $("#startID").prop("disabled", false);
            $("#stopID").prop("disabled", true);

            $("#startID").click(function () {
                $("#startID").prop("disabled", true);
                $("#stopID").prop("disabled", false);

                startId = setInterval(function () {
                    // 生成随机数0-6
