package com.daliu.dao;

import com.daliu.domain.Account;

import java.util.List;

/**
 * 帐户持久层接口
 */
public interface IAccountDao {
    /// 查询所有
    List findAllAccount();
    /// 查询一个
    Account findAccountById(Integer accountId);
