            ComboPooledDataSource ds = new ComboPooledDataSource();

            ds.setDriverClass(driver);
            ds.setJdbcUrl("jdbc:mysql://localhost:3306/daliu_2?characterEncoding=utf-8&useSSL=false&serverTimezone=UTC");
            ds.setUser(username);
