com.daliu.utils.ConnectionUtils.java
package com.daliu.utils;

import javax.sql.DataSource;
import java.sql.Connection;

/**
 * 连接的工具类，用于从数据源中获取一个连接并且实现和线程的绑定
 */
public class ConnectionUtils {
    private ThreadLocal threadLocal = new ThreadLocal();

    /// 由spring注入
    private DataSource dataSource;

    public void setDataSource(DataSource dataSource) {
        this.dataSource = dataSource;
    }

    public DataSource getDataSource() {
        return dataSource;
    }

    /**
     * 获取当前线程上的连接
     * @return 数据库连接
     */
    public Connection getThreadConnection() {
        // 1. 先从ThreadLocal中获取
        Connection conn = threadLocal.get();
        // 2. 判断当前线程上是否有连接
        if (conn == null) {
            try {
                // 3. 从数据源中获取连接并且存入ThreadLocal中
                conn = dataSource.getConnection();
                threadLocal.set(conn);
            } catch (Exception ex) {
                ex.printStackTrace();
                throw new RuntimeException(ex);
            }
        }
        return conn;
    }

    /**
     * 把连接和线程解绑
     */
    public void removeConnection() {
        threadLocal.remove();
    }
}

com.daliu.utils.TransactionManager.java
package com.daliu.utils;

/**
 * 和事务管理相关的工具类
 * 开启事务、提交事务、回滚事务、释放连接
 */
public class TransactionManager {
    /// 由spring注入
    private ConnectionUtils connectionUtils;

    public void setConnectionUtils(ConnectionUtils connectionUtils) {
        this.connectionUtils = connectionUtils;
    }

    /**
     * 开启连接
     */
    public void beginTransaction() {
        try {
            connectionUtils.getThreadConnection().setAutoCommit(false);
        } catch (Exception ex) {
            ex.printStackTrace();
        }
    }

    /**
     * 提交事务
     */
    public void commit() {
        try {
            connectionUtils.getThreadConnection().commit();
        } catch (Exception ex) {
            ex.printStackTrace();
        }
    }

    /**
     * 回滚事务
     */
    public void rollback() {
        try {
            connectionUtils.getThreadConnection().rollback();
        } catch (Exception ex) {
            ex.printStackTrace();
        }
    }

    /**
     * 释放连接
     */
    public void release() {
        try {
            connectionUtils.getThreadConnection().close(); // 还回到连接池
            connectionUtils.removeConnection(); // 由于connection被close掉了，下次再从连接池中获取关闭的connection不可用，这里直接删除掉
        } catch (Exception ex) {
            ex.printStackTrace();
        }
    }
}

